import Draggable from 'react-draggable';
import Piggy from '../../assets/piggy.png';
import DubCoin from '../../assets/dub_coin.png';
import { draggableContainerStyle, dubCoinStyle, gradientBoxStyle, messageContainerStyle, messageStyle, piggyContainerStyle, piggySlideTrackContainerStyle, piggyStyle } from './piggySlideTrackStyles';
import { useState } from 'react';
import React from 'react';
import { cx } from '@emotion/css';
import { ReactComponent as Chevron } from '../../assets/chevron.svg';

interface Position {
  x: number;
  y: number;
}
// export const PiggySlideTrack = () => {
  export class PiggySlideTrack extends React.Component {

   state = {
    activeDrags: 0,
    deltaPosition: {
      x: 0, y: 0
    },
    controlledPosition: {
      x: -400, y: 200
    },
    isSuccess: false,
  };

  handleDrag = (e: any, ui: any) => {
    const {x, y} = this.state.deltaPosition;
    this.setState({
      deltaPosition: {
        x: x + ui.deltaX,
        y: y + ui.deltaY,
      }
    });
  };

   onStart = () => {
    this.setState({activeDrags: ++this.state.activeDrags});
  };

  onStop = () => {
    console.log('onStop: ', this.state.deltaPosition.y);
    if (this.state.deltaPosition.y > 300) {
      console.log('YAY')
      this.setState({isSuccess: true})
    }
    this.setState({activeDrags: --this.state.activeDrags, deltaPosition: {
      x: 0, y: 0
    }});


  };
  onDrop = (e: any) => {
    this.setState({activeDrags: --this.state.activeDrags});
    if (e.target.classList.contains("drop-target")) {
      alert("Dropped!");
      e.target.classList.remove('hovered');
    }
  };
   onDropAreaMouseEnter = (e: any) => {
    if (this.state.activeDrags) {
      e.target.classList.add('hovered');
    }
  }
   onDropAreaMouseLeave = (e: any) => {
    e.target.classList.remove('hovered');
  }

  // For controlled component
   adjustXPos = (e: any) => {
    e.preventDefault();
    e.stopPropagation();
    const {x, y} = this.state.controlledPosition;
    this.setState({controlledPosition: {x: x - 10, y}});
  };

   adjustYPos = (e: any) => {
    e.preventDefault();
    e.stopPropagation();
    const {controlledPosition} = this.state;
    const {x, y} = controlledPosition;
    this.setState({controlledPosition: {x, y: y - 10}});
  };

   onControlledDrag = (e: any, position: any) => {
    const {x, y} = position;
    this.setState({controlledPosition: {x, y}});
  };

   onControlledDragStop = (e: any, position: any) => {
    this.onControlledDrag(e, position);
    this.onStop();
  };

  render() {
    const dragHandlers = {onStart: this.onStart, onStop: this.onStop};
    const {deltaPosition, controlledPosition} = this.state;

    return (
      <div className={piggySlideTrackContainerStyle}>

        <div className={gradientBoxStyle(deltaPosition.y, this.state.isSuccess)} >
        <h1 style={{color: 'green', zIndex: 15}}>{deltaPosition.y.toFixed(0)}</h1>
        </div>
        <div className={messageContainerStyle}>
          <div className={messageStyle}>Slide down for a dub</div>
          <Chevron />
        </div>
        <div className={piggyContainerStyle}>
          <img src={Piggy} className={piggyStyle} draggable="false" />
        </div>
        <Draggable axis="y" onDrag={this.handleDrag} position={{x: 0, y: deltaPosition.y}} {...dragHandlers} >
          <img draggable="false" className={cx(dubCoinStyle(this.state.isSuccess), 'handle')} src={DubCoin} width={150} style={{zIndex: 10}} />
        </Draggable>

      </div>
    );
  }
}
