import Draggable from 'react-draggable';
import Piggy from '../../assets/piggy.png';
import DubCoin from '../../assets/dub_coin.png';
import QrCode from '../../assets/qrcode.png';
import { 
  dubCoinStyle, 
  gradientBoxStyle, 
  messageContainerStyle, 
  messageStyle, 
  piggyContainerStyle, 
  piggySlideTrackContainerStyle, 
  piggyStyle, 
} from './piggySlideTrackStyles';
import { useEffect, useState } from 'react';
import { cx } from '@emotion/css';
import { ReactComponent as Chevron } from '../../assets/chevron.svg';

export interface Position {
  x: number;
  y: number;
}

export const PiggySlideTrack = () => {
  const { innerHeight: height } = window;

  const [coinPosition, setCoinPosition] = useState<Position>({ x: 0, y: 0 });
  const [didCoinEnter, setDidCointEnter] = useState<boolean>(false);
  const [coinEnterPosition, setCoinEnterPosition] = useState<number>(height - 100);

  const piggyPosition = document.getElementById('piggy')?.getBoundingClientRect();

  useEffect(() => {
    if (piggyPosition) {
      setCoinEnterPosition(piggyPosition.top - piggyPosition.height / 2 + 20)
    }
  });

  const handleDrag = (e: any, ui: { deltaX: number; deltaY: number; }) => {
    const { x, y } = coinPosition;
    setCoinPosition({
      x: x + ui.deltaX,
      y: y + ui.deltaY,
    });
  };

  const onStop = () => {
    if (coinPosition.y > coinEnterPosition) {
      setDidCointEnter(true);
    }
    setCoinPosition({ x: 0, y: 0 });
  };

  const onPiggyClick = () => {
    setDidCointEnter(false);
  }

  const dragHandlers = { onStop: onStop, onDrag: handleDrag };
  console.log('coinEnterPosition ', coinEnterPosition);
  return (
    <div className={piggySlideTrackContainerStyle}>
      <div className={messageContainerStyle}>
        <div className={messageStyle}>Slide down for a dub</div>
        <Chevron />
      </div>
      <div className={gradientBoxStyle(coinPosition.y, didCoinEnter)} />

      <div className={piggyContainerStyle}>
        <img id="piggy" src={Piggy} className={piggyStyle} draggable="false" onClick={onPiggyClick} />
      </div>
      <Draggable axis="y" position={{x: 0, y: coinPosition.y}} {...dragHandlers} >
        <img draggable="false" className={cx(dubCoinStyle(didCoinEnter), 'handle')} src={DubCoin} width={150} style={{zIndex: 10}} />
      </Draggable>
      <div>
        <img src={QrCode} width={200} />
      </div>
    </div>
  );
}
