  // };

  // const handleDrag = (e: any, ui: any) => {
  //   const { x, y } = deltaPosition;
  //   setDeltaPosition({
  //     // deltaPosition: {
  //       x: x + ui.deltaX,
  //       y: y + ui.deltaY,
  //     // }
  //   });
  // };

  const onStart = () => {
    setActiveDrags(activeDrags + 1);
  };

  const onStop = () => {
    setActiveDrags(activeDrags - 1);
  };
  const onDrop = (e: any) => {
    setActiveDrags(activeDrags - 1);

    if (e.target.classList.contains("drop-target")) {
      alert("Dropped!");
      e.target.classList.remove('hovered');
    }
  };

  // const handleStart = () => {};
  // const handleDrag = () => {};
  // const handleStop = () => {};


  return (
    <div className={piggySlideTrackContainerStyle}>
      <div>
      <Draggable
        nodeRef={nodeRef}
        axis="y"
        // handle=".handle"
        // defaultPosition={{x: 0, y: 0}}
        // position={null}
        // grid={[25, 25]}
        // scale={1}
        onStart={onStart}
        // onDrag={onDrag}
        onStop={onStop}>
        {/* <div> */}
          <img src={DubCoin} width={150} />
          {/* <div className="handle">Drag from here</div>
