import { css } from '@emotion/css';

export const homePageContainerStyle = css({
  position: 'relative',
  height: '100%',
});

export const piggyStyle = css({
  width: 500,
  position: 'absolute',
  bottom: '-26%',
  left: '50%',
  transform: 'translate(-50%, 0)',

  ':hover': {
    transform: 'translate(-50%, 0) scale(50)',
  },

  '@media (max-width: 420px)': {
    width: '80%',
    bottom: '-15%',
  }
})
