import Draggable from 'react-draggable';
import Piggy from '../../assets/piggy.png';
import DubCoin from '../../assets/dub_coin.png';
import { piggyContainerStyle, piggySlideTrackContainerStyle, piggyStyle } from './piggySlideTrackStyles';
import { useState } from 'react';

interface Position {
  x: number;
  y: number;
}
export const PiggySlideTrack = () => {

  const [activeDrags, setActiveDrags] = useState(0);
  const [deltaPosition, setDeltaPosition] = useState<Position>();
  // const [activeDrags, setActiveDrags] = useState();
  const [controlledPosition, setControlledPosition] = useState<Position>();

  // state = {
  //   activeDrags: 0,
  //   deltaPosition: {
  //     x: 0, y: 0
  //   },
  //   controlledPosition: {
  //     x: -400, y: 200
  //   }
  // };

  const handleDrag = (e, ui) => {
    const { x, y } = deltaPosition;
    setDeltaPosition({
      // deltaPosition: {
        x: x + ui.deltaX,
        y: y + ui.deltaY,
      // }
    });
  };

  const onStart = () => {
    setActiveDrags(activeDrags + 1);
  };

  const onStop = () => {
    setActiveDrags(activeDrags - 1);
  };
  const onDrop = (e) => {
    this.setState({activeDrags: --activeDrags});
    if (e.target.classList.contains("drop-target")) {
      alert("Dropped!");
      e.target.classList.remove('hovered');
    }
  };

  // const handleStart = () => {};
  // const handleDrag = () => {};
  // const handleStop = () => {};


  return (
    <div className={piggySlideTrackContainerStyle}>
      <div>
      <Draggable
        axis="y"
        // handle=".handle"
        // defaultPosition={{x: 0, y: 0}}
        // position={null}
        // grid={[25, 25]}
        // scale={1}
        onStart={handleStart}
        onDrag={handleDrag}
        onStop={handleStop}>
        {/* <div> */}
          <img src={DubCoin} width={150} />
          {/* <div className="handle">Drag from here</div>
          <div>This readme is really dragging on...</div> */}
        {/* </div> */}
      </Draggable>
      </div>
      <div className={piggyContainerStyle}>
        <img src={Piggy} className={piggyStyle} />
      </div>
    </div>
  )
}
