import { css } from '@emotion/css';
import { Colors } from '../../styles/colors';
import { Fonts } from '../../styles/fonts';

export const piggySlideTrackContainerStyle = css({
  // height: '100%',
  // width: '100%',
  // overflow: 'hidden',
  // position: 'absolute',
  // bottom: 0,
  // left: '50%',
  // transform: 'translate(-50%, 0)',
});

export const piggyContainerStyle = css({
  position: 'relative',
  bottom: 0,
  height: '100%',
});

export const piggyStyle = css({
  width: 500,
  position: 'fixed',
  bottom: 0,
  left: '50%',
  transform: 'translate(-50%, 40%)',
  transition: 'all 0.5s ease',

  ':hover': {
    transform: 'translate(-50%, 0) scale(1.07)',
  },

  '@media (max-width: 420px)': {
    width: '80%',
    bottom: '-15%',
  }
});

export const draggableContainerStyle = css({
  zIndex: 10,
});

export const dubCoinStyle = (isSuccess?: boolean) => css({
  opacity: isSuccess ? 0 : 1,
})

export const gradientBoxStyle = (height: number, isSuccess?: boolean) => css({
  position: 'fixed',
  top: 0,
  background: Colors.Branding.Gradient,
  width: '100%',
  transition: isSuccess ? 'all 0.3s ease' : 'none', 
  height: isSuccess ? '100%' : height + 188,
})

export const messageContainerStyle = css({
  position: 'fixed',
});

export const messageStyle = css(Fonts.SubHeading.SH6, {
  color: Colors.White
});
