import { draggableContainerStyle, dubCoinStyle, gradientBoxStyle, messageContainerStyle, messageStyle, piggyContainerStyle, piggySlideTrackContainerStyle, piggyStyle } from './piggySlideTrackStyles';
import { useState } from 'react';
import React from 'react';
import { cx } from '@emotion/css';
import { ReactComponent as Chevron } from '../../assets/chevron.svg';

export interface Position {
  x: number;
  y: number;
}

export const PiggySlideTrack = () => {
  // export class PiggySlideTrack extends React.Component {
  const [coinPosition, setCoinPosition] = useState<Position>({ x: 0, y: 0 });
  const [didCoinEnter, setDidCointEnter] = useState<boolean>(false);

   state = {
    activeDrags: 0,
    deltaPosition: {
      x: 0, y: 0
    },
    // controlledPosition: {
    //   x: -400, y: 200
    // },
    isSuccess: false,
  };

  const handleDrag = (e: any, ui: any) => {
    const { x, y } = coinPosition;
    setCoinPosition({
      x: x + ui.deltaX,
      y: y + ui.deltaY,
    });
  };

   const onStart = () => {
    // this.setState({activeDrags: ++this.state.activeDrags});
  };

  const onStop = () => {
    console.log('onStop: ', coinPosition.y);
    if (coinPosition.y > 300) {
      console.log('YAY')
      setDidCointEnter(true);
    }
    setCoinPosition({ x: 0, y: 0 });


  };
  onDrop = (e: any) => {
    this.setState({activeDrags: --this.state.activeDrags});
    if (e.target.classList.contains("drop-target")) {
      alert("Dropped!");
      e.target.classList.remove('hovered');
    }
  };
  //  onDropAreaMouseEnter = (e: any) => {
  //   if (this.state.activeDrags) {
  //     e.target.classList.add('hovered');
  //   }
  // }
  //  onDropAreaMouseLeave = (e: any) => {
  //   e.target.classList.remove('hovered');
  // }

  // For controlled component
  //  adjustXPos = (e: any) => {
  //   e.preventDefault();
  //   e.stopPropagation();
  //   const {x, y} = this.state.controlledPosition;
  //   this.setState({controlledPosition: {x: x - 10, y}});
  // };

  //  adjustYPos = (e: any) => {
  //   e.preventDefault();
  //   e.stopPropagation();
  //   const {controlledPosition} = this.state;
  //   const {x, y} = controlledPosition;
  //   this.setState({controlledPosition: {x, y: y - 10}});
  // };

  //  onControlledDrag = (e: any, position: any) => {
  //   const {x, y} = position;
  //   this.setState({controlledPosition: {x, y}});
  // };

  //  onControlledDragStop = (e: any, position: any) => {
  //   this.onControlledDrag(e, position);
  //   this.onStop();
  // };

  // render() {
    const dragHandlers = {onStart: onStart, onStop: onStop, onDrag: handleDrag};
    // const {deltaPosition} = this.state;

    return (
      <div className={piggySlideTrackContainerStyle}>
        <div className={messageContainerStyle}>
          <div className={messageStyle}>Slide down for a dub</div>
          <Chevron />
        </div>
        <div className={gradientBoxStyle(coinPosition.y, didCoinEnter)} >
        <h1 style={{color: 'green', zIndex: 15}}>{coinPosition.y.toFixed(0)}</h1>
        </div>

        <div className={piggyContainerStyle}>
          <img src={Piggy} className={piggyStyle} draggable="false" />
        </div>
        <Draggable axis="y" position={{x: 0, y: coinPosition.y}} {...dragHandlers} >
          <img draggable="false" className={cx(dubCoinStyle(didCoinEnter), 'handle')} src={DubCoin} width={150} style={{zIndex: 10}} />
        </Draggable>

      </div>
    );
  // }
}
